Classroom.Courses.Students.createBulk('courseId', ['userId1', 'userId2']);



Classroom.Courses.Students.removeBulk('courseId', ['userId1', 'userId2']);



Classroom.Courses.Topics.createBulk('courseId', ['Topic1', 'Topic2']);



